import React from 'react'
import styled from 'styled-components'
import { Box, Flex, Text } from '@blockstack/ui'
import { userSession } from '@stores/user'

import { MakerNavContainer } from './nav-layout'
import { Arrow } from '@components/arrow'

const SelectBox = styled(Box)`
  -webkit-appearance: none;
  -moz-appearance: none;
  appearance: none;
  border: 0;
  background: transparent;
  font-size: 14px;
  font-weight: 500;
  padding: 0;
  width: auto;
  &:focus {
    outline: 0;
  }
`

interface AppSelectProps {
  selectedValue?: number
  onChange?(e: any): void
  apps?: any[]
}

const AppSelect = ({ selectedValue, onChange, apps = [] }: AppSelectProps) => (
  <Flex alignItems="center">
    <Box>
      <Flex>
        <Box mt="2px" mx={2}>
          <img width="16px" height="16px" src="data:image/png;base64," />
        </Box>
        <Flex alignItems="center">
          {apps.length && (
            <select value={selectedValue} onChange={onChange}>
              {apps.map(({ name, id }) => (
                <option key={id} value={id}>
                  {name}
                </option>
              ))}
            </select>
          )}
          <SelectBox as="select" onChange={onChange}>

          </SelectBox>
          {apps.length > 1 && <Arrow direction="down" />}
        </Flex>
      </Flex>
    </Box>
  </Flex>
)


interface MakerNavActionsProps {
  userId: string
  appName?: string
  handleSignOut(): void;
  apps?: {
    name: string
    value: number
  }[]
}

const MakerNavActions = ({ apps, userId, appName, handleSignOut }: MakerNavActionsProps) => {
  const User = () => <Text fontSize={0}>{userId}</Text>
  return (
    <Flex alignItems="center">
      <Box textAlign="right" mr={3}>
        <User />
      </Box>
      <Box textAlign="right" mr={1}>
        <Text onClick={handleSignOut} as="a" fontSize={0} fontWeight="medium" color="blue.900">Sign out</Text>
      </Box>
    </Flex>
  )
}

interface MakerNavProps {
  apps: any[]
  userId: string
  onChange?(e: Event): void;
  handleSignOut(): void;
}

export const MakerNav = ({ apps, userId, onChange, handleSignOut }: MakerNavProps) => {
  return (
    <MakerNavContainer>

      <AppSelect apps={apps} onChange={onChange} selectedValue={28} />
      <MakerNavActions appName="klsdflksdf" userId={userId} handleSignOut={handleSignOut} />

    </MakerNavContainer>
  )
}
